import React, { useContext } from 'react';
import StarWarsContext from '../context/StarWarsContext';

function SortInput() {
  const {
    setSortAscOrDesc,
    sortColumn,
    sortAscOrDesc,
    planets,
    setPlanets } = useContext(StarWarsContext);
  const descending = -1;

  function sortFilter(column, type) {
    const sortType = type === 'ASC' ? 1 : descending;
    const sortedColumn = [...planets]
      .sort((planetA, planetB) => ((Number(planetA[column]) > Number(planetB[column])
        ? sortType
        : -sortType)));
    setPlanets(sortedColumn);
  }

  return (
    <div>
      <label htmlFor="asc-radio">
        ASC
        <input
          data-testid="column-sort-input-asc"
          name="sort-radios"
          id="asc-radio"
          type="radio"
          value="ASC"
          onClick={ () => setSortAscOrDesc('ASC') }
        />
      </label>
      <label htmlFor="desc-radio">
        DESC
        <input
          data-testid="column-sort-input-desc"
          name="sort-radios"
          id="desc-radio"
          type="radio"
          value="DESC"
          onClick={ () => setSortAscOrDesc('DESC') }
        />
      </label>
      <button
        onClick={ () => sortFilter(sortColumn, sortAscOrDesc) }
        data-testid="column-sort-button"
        type="button"
      >
        Filtrar
      </button>
    </div>
  );
}

export default SortInput;
